		// 			alt="Product Image" />
		// 		<div class="card-body">
		// 			<h5 class="card-title">{products.title} - {products.price}</h5>
		// 			<p class="card-text">{products.description}</p>
		// 			<a href="#" class="btn btn-primary buynow" data-id="${product.id}">Buy now</a>
		// 		</div>
		// 	</div>
		// </div>
	)
}

const product = [
	{ id: 1, title: 'Product 1', description: 'This is product 1', image: '/images/product1.png' },
	{ id: 2, title: 'Product 2', description: 'This is product 2', image: '/images/product2.png' },
	{ id: 3, name: 'Product 3', description: 'This is product 3', image: '/images/product3.png' },
	{ id: 4, name: 'Product 4', description: 'This is product 4', image: '/images/product4.png' },
  ];

function App() {
	return (
		<div className="App">
			<Search products={product} />
		</div>
	);
}

export default Search
